mapper.select:
		SELECT sft.* FROM ssm.sys_function_t sft join 
		(select srft.* from sys_role_function_t srft join sys_role_t sr
		on srft.ROLE_ID = sr.ROLE_ID and
		
			
				sr.ROLE_ID= #{roleId}
			
			
				sr.ROLE_NAME= #{roleName}
			
		
		) as t2
		on sft.function_id=t2.function_Id
